import React from "react";
import Card from "../Card";

export const BoardOfDirectors = () => {
  // Create an array of data for multiple directors (you can replace this with your data)
  const directorsData = [
    { name: "John Doe", department: "IT", campus: "New York" },
    { name: "John Doe", department: "IT", campus: "New York" },
    // Add more director data as needed
  ];

  return (
    <div>
      <h1>Board of Directors</h1>
      <div className="styles">
        {directorsData.map((director, index) => (
          <Card
            key={index}
            name={director.name}
            department={director.department}
            campus={director.campus}
          />
        ))}
      </div>
    </div>
  );
};

const styles = `
  .card-container {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    gap: 20px; /* Adjust the gap as needed */
  }
`;

// Attach the CSS to the component
const styleSheet = document.createElement("style");
styleSheet.type = "text/css";
styleSheet.innerText = styles;
document.head.appendChild(styleSheet);
