import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./NavBar.css";
import logo from "../../logo-FAST-NU.png";

function NavBar() {
  const [click, setClick] = useState(false);

  const handleClick = () => setClick(!click);
  return (
    <>
      <nav className="navbar w-full bg-gray-800 text-gray-100 body-font mb-4 shadow-sm">
        <div className="nav-container container mx-auto flex justify-between items-center py-7 px-5">
          <NavLink exact to="/" className="nav-logo flex flex-shrink-0 title-font font-medium items-center text-gray-900 md:mb-0">
            <img className="FAST-logo w-10 h-10 text-white p-2 bg-gradient-to-br from-pink-500 to-yellow-500 rounded-full" src={logo} alt="FAST NUCES Logo" />
            Career Development Portal
            <i className="fas fa-code ml-3 text-xl text-gray-100 font-semibold antialiased"></i>
          </NavLink>

          <ul className={click ? "nav-menu active hidden md:ml-auto md:flex flex-wrap items-center justify-center text-base tracking-wide" : "nav-menu"}>
            {/* Add the rest of the links here with the same styling as the first component */}
          </ul>
          <div className="nav-icon hidden sm:inline-flex ml-auto md:ml-0 mr-4 md:mr-0 cursor-pointer" onClick={handleClick}>
            <i className={click ? "fas fa-times h-8 w-8" : "fas fa-bars h-8 w-8"}></i>
          </div>
        </div>
      </nav>
    </>
  );
}

export default NavBar;
